public class ValidatorRegistry : IValidatorRegistry
{
    private readonly List<Assembly> _registeredAssemblies = new List<Assembly>();
    private readonly IServiceProvider _serviceProvider;

    public ValidatorRegistry(IServiceProvider serviceProvider)
    {
        _serviceProvider = serviceProvider;
    }

    public void ClearRegisteredAssemblies()
    {
        _registeredAssemblies.Clear();
    }

    public IEnumerable<Assembly> GetRegisteredAssemblies()
    {
        return _registeredAssemblies;
    }

    public void RegisterAssembly(Assembly assembly)
    {
        if (!_registeredAssemblies.Contains(assembly))
        {
            _registeredAssemblies.Add(assembly);
            RegisterValidators(assembly);
        }
    }

    public void RegisterAssemblyByPartialName(string partialName)
    {
        var assemblies = AppDomain.CurrentDomain.GetAssemblies()
            .Where(a => a.FullName?.Contains(partialName) ?? false)
            .ToList();

        foreach (var assembly in assemblies)
        {
            RegisterAssembly(assembly);
        }
    }

    private void RegisterValidators(Assembly assembly)
    {
        var allTypes = assembly.GetExportedTypes()
            .Where(t => t.IsClass && !t.IsAbstract);

        var validatorTypes = allTypes.Where(t => t.GetInterfaces().Any(i => i.Name == "IEntityValidator`1"));
        foreach (var type in validatorTypes)
        {
            try
            {
                var instance = Activator.CreateInstance(type);
                var inter = type.GetInterface("IEntityValidator`1");
                var entity = inter.GetTypeInfo().GenericTypeArguments[0];

                ValidatorFactory.RegisterValidator(entity.Name, instance);
            }
            catch (Exception ex)
            {
                // Handle exception (log or rethrow)
            }
        }

        var fluentValidatorTypes = allTypes.Where(t => t.BaseType.IsGenericType
                && t.BaseType.GetGenericTypeDefinition() == typeof(AbstractValidator<>)
                && !t.ContainsGenericParameters);

        var fvType = typeof(FluentValidatorAdapter<>);
        foreach (var type in fluentValidatorTypes)
        {
            var instance = Activator.CreateInstance(type);
            var abstr = type.BaseType;
            var entity = abstr.GenericTypeArguments[0];

            var constructed = fvType.MakeGenericType(entity);
            var validator = Activator.CreateInstance(constructed, args: instance);

            ValidatorFactory.RegisterValidator(entity.Name, validator);
        }
    }
}
