using System.Reflection;
using FluentValidation;
using Microsoft.Extensions.DependencyInjection;

public class ValidatorRegistry : IValidatorRegistry
{
    private readonly IServiceCollection _services;

    public ValidatorRegistry(IServiceCollection services)
    {
        _services = services;
    }

    public void RegisterValidators(Assembly assembly)
    {
        var validatorTypes = assembly.GetExportedTypes()
            .Where(t => t.IsClass && !t.IsAbstract && typeof(IValidator).IsAssignableFrom(t));

        foreach (var type in validatorTypes)
