        private static readonly List<Tuple<string, object>> _validators = new List<Tuple<string, object>>();

        static ValidatorFactory()
        {
            var registerValidators = new RegisterValidators(_validators);
            registerValidators.LoadValidators(PlankValidatorConfiguration.GetRegisteredAssemblies());
        }

        public IEnumerable<IValidator> CreateInstance(Type type)
        {
            return _validators
                .Where(v => v.Item1 == type.Name)
                .Select(v => (IValidator)v.Item2)
                .OrderBy(v => v.Priority);
        }

        public IEnumerable<IEntityValidator<TEntity>> CreateInstance<TEntity>() where TEntity : IEntity
        {
            return _validators
                .Where(v => v.Item1 == typeof(TEntity).Name)
                .Select(v => (IEntityValidator<TEntity>)v.Item2)
                .OrderBy(v => v.Priority);
        }

        public static void RegisterValidator(string entityName, object validator)
        {
            if (!_validators.Any(v => v.Item1 == entityName && v.Item2.GetType() == validator.GetType()))
