        }

        public static IMapper CreateMapper<TEntity, TDto>()
            where TEntity : IEntity, new()
            where TDto : IDto
        {
            var config = new MapperConfiguration(cfg =>
            {
                // This line ensures that internal properties are also mapped over.
                cfg.ShouldMapProperty = p => (p.GetMethod?.IsPublic ?? false) || (p.GetMethod?.IsAssembly ?? false);
                cfg.AddProfile<PlankMapperProfile<TEntity, TDto>>();

                foreach (var profile in _profiles)
                {
                    cfg.AddProfile(profile);
                }
            });

            return config.CreateMapper();
        }

        private static IEnumerable<Assembly> GetRegisteredAssemblies()
        {
            // Return the list of assemblies to scan for profiles
            return new List<Assembly> { Assembly.GetExecutingAssembly() };
        }
    }
}
