```csharp
using FluentValidation;
using log4net;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Reflection;

namespace Plank.Core.Validators
{
    public class ValidatorRegistrar
    {
        private readonly List<Tuple<string, object>> _validators;
        private readonly ILog _logger;

        public ValidatorRegistrar(List<Tuple<string, object>> validators, ILog logger)
        {
            _validators = validators;
            _logger = logger;
        }

        public void RegisterValidators(IEnumerable<Assembly> assemblies)
        {
            var allTypes = GetAllTypes(assemblies);
            RegisterValidators(allTypes);
        }

        public void RegisterValidator(Type validatorType)
        {
            if (typeof(IValidator).IsAssignableFrom(validatorType) || typeof(IEntityValidator).IsAssignableFrom(validatorType))
            {
                AddValidator(validatorType);
            }
        }

        private IEnumerable<Type> GetAllTypes(IEnumerable<Assembly> assemblies)
        {
            return assemblies
                .SelectMany(a => a.GetExportedTypes())
                .Where(t => t.IsClass && !t.IsAbstract);
        }

        private void RegisterValidators(IEnumerable<Type> allTypes)
        {
            var validatorTypes = allTypes.Where(t => typeof(IValidator).IsAssignableFrom(t) || typeof(IEntityValidator).IsAssignableFrom(t));
            foreach (var type in validatorTypes)
            {
                AddValidator(type);
            }
        }

        private void AddValidator(Type validatorType)
        {
            try
            {
                if (_validators.Any(v => v.Item2.GetType() == validatorType))
                {
                    return;
                }

                var validator = Activator.CreateInstance(validatorType);
                _validators.Add(new Tuple<string, object>(validatorType.Name, validator));
            }
            catch (Exception ex)
            {
                _logger.Error(ex);
                throw;
            }
        }
    }
}
