using AutoMapper;

namespace Plank.Core.Mappers
{
    public static class PlankMapperExtensions
    {
        public static TDto Map<TEntity, TDto>(this IMapper mapper, TEntity source)
        {
            return mapper.Map<TDto>(source);
        }

        public static ApiGetResponseDto<TDto> Map<TEntity, TDto>(this IMapper mapper, ApiGetResponseDto<TEntity> source)
        {
            return mapper.Map<ApiGetResponseDto<TDto>>(source);
        }

        public static ApiPostResponseDto<TDto> Map<TEntity, TDto>(this IMapper mapper, ApiPostResponseDto<TEntity> source)
        {
            return mapper.Map<ApiPostResponseDto<TDto>>(source);
        }

        public static ApiDeleteResponseDto Map<TEntity, TDto>(this IMapper mapper, ApiDeleteResponseDto source)
        {
            return mapper.Map<ApiDeleteResponseDto>(source);
        }
    }
}
